import React, { Component } from 'react';

class ModalLogic extends Component {
    constructor(props) {
      super(props);
      this.state = {
        clicks:0,
        show:true
      };
    }

  IncrementItem = () => {
    this.setState({ clicks: this.state.clicks + 1 });
  }
  DecreaseItem = () => {
    this.setState({ clicks: this.state.clicks - 1 });
  }
  ToggleClick = () => {
    this.setState({ show: !this.state.show });
  }

    render() {
        return (
            <div className="col d-inline-flex fs-3">
                <div className=""><button onClick={this.DecreaseItem} className="border-1 bg-light" style={{width: "35px"}}>-</button></div>
                <div><button className="border-1 bg-light" style={{width: "35px", pointerEvents: "none"}}>{this.state.clicks}</button></div>
                <div><button onClick={this.IncrementItem} className="border-1 bg-light" style={{width: "35px"}}>+</button></div>
            </div> 
        );
    }
