import React, { Component } from 'react';

class ModalLogic extends Component {
    constructor(props) {
      super(props);
      this.state = {
        clicks:0,
        show:true
      };
    }

  IncrementItem = () => {
    this.setState({ clicks: this.state.clicks + 1 });
  }
  DecreaseItem = () => {
    this.setState({ clicks: this.state.clicks - 1 });
  }
  ToggleClick = () => {
    this.setState({ show: !this.state.show });
  }

