                <label>ProductID<br></br>
                <input type="text" name="ProductID" /></label>
                <label>Name<br></br>
                <input type="text" name="Name" /></label>
                <label>Price<br></br>
                <input type="text" name="Price" /></label>
                <label>Description<br></br>
                <input type="text" name="Description" /></label>
                <label>Image<br></br>
                <input type="text" name="Image" /></label>
                <label>Category<br></br>
                <input type="text" name="Category" /></label>
            </div>
            <button>Submit</button>
        </div>
    )
}

export function Home() {
    return (
        <>
        <UpperSide />
        <UpperSideSecond />
        <Slideshow></Slideshow>
        <Main></Main>
        <Footer year={new Date().getFullYear()}></Footer>
        </>
    )
}

export function Whoops404() {
    let location = useLocation();

    return(
        <div>
            <h1>Resource not found at {location.pathname}!</h1>
        </div>
    )
}
