import React, { useState } from "react"
import {Link, useLocation} from "react-router-dom"
import {UpperSide, UpperSideSecond, Slideshow, Main, MainMenu, CartOpened, Checkout, Footer, CartNotOpened} from "./index.js"

export function Menu() {
    // useState to show the cart
    const [popUp, setPopUp] = useState("cart");

        {/* Conditional rendering for showing the cart */}
        if (popUp == "noCart") {
            return(
            <>
                <UpperSide />       
                <MainMenu />
                <Link to="/">Home</Link>
                <CartNotOpened setPopUp={ () => setPopUp("cart") } />
            </>
            )
        }
        else if (popUp == "cart") { return <CartOpened setPopUp={() => setPopUp("noCart")} setPopUpCheckout={() => setPopUp("checkout")} /> }
        else { return <Checkout setPopUp={() => setPopUp("cart")} /> }
        
}

export function Mysql() {
    return(
        <div className="bg-white">
            <h1>Here will do the Mysql stuff</h1>

            <div className="d-flex justify-content-center align-items-center ">
                <label>users table</label>
                <label>Username</label>
                <input type="text" name="Username" />
                <label>Password</label>
                <input type="text" name="Password" />
                <label>Email</label>
                <input type="text" name="Email" />
                <label>Fullname</label>
                <input type="text" name="Fullname" />
                <label>Address</label>
                <input type="text" name="Address" />
                <label>Phone</label>
                <input type="text" name="Phone" />

                <button>Submit</button>
            </div>
        </div>
    )
}

export function Home() {
    return (
        <>
        <UpperSide />
        <UpperSideSecond />
        <Slideshow></Slideshow>
        <Main></Main>
        <Footer year={new Date().getFullYear()}></Footer>
        </>
    )
}

export function Whoops404() {
    let location = useLocation();

    return(
        <div>
            <h1>Resource not found at {location.pathname}!</h1>
        </div>
    )
}
