import React, { Component, useState } from 'react';
import { CartNotOpened } from '..';


function ModalLogic(setPopUp) {
    console.log(setPopUp)
    // useState for clicks
    const [clicks, setClicks] = useState(1);

    // useState for cartItems
    const [cartItems, setCartItems] = useState(0);

    function IncrementItem() {
        setClicks(parseInt(clicks) + 1); 
    }

    function DecreaseItem() {
        if (parseInt(clicks) === 1) return;
        setClicks(parseInt(clicks) - 1);
    }

    function AddToCart() {
        setCartItems(parseInt(cartItems) + parseInt(clicks));
    }

    // IncrementItem = (clicks) => {
