import React, { useState, useEffect } from "react"
import {Link, useLocation} from "react-router-dom"
import Axios from 'axios'
import {UpperSide, UpperSideSecond, Slideshow, Main, MainMenu, CartOpened, Checkout, Footer, CartNotOpened} from "./index.js"

export function Menu() {
    // useState to show the cart
    const [popUp, setPopUp] = useState("cart");

        {/* Conditional rendering for showing the cart */}
        if (popUp == "noCart") {
            return(
            <>
                <UpperSide />       
                <MainMenu />
                <Link to="/">Home</Link>
                <CartNotOpened setPopUp={ () => setPopUp("cart") } />
            </>
            )
        }
        else if (popUp == "cart") { return <CartOpened setPopUp={() => setPopUp("noCart")} setPopUpCheckout={() => setPopUp("checkout")} /> }
        else { return <Checkout setPopUp={() => setPopUp("cart")} /> }
        
}

export function Mysql() {
    const [ProductID, setProductID] = useState("");
    const [Name, setName] = useState("");
    const [Price, setPrice] = useState("");
    const [Description, setDescription] = useState("");
    const [Image, setImage] = useState("");
    const [Category, setCategory] = useState("");

    // The below useEffect is used to display on the front-end all my info from db(Just as an example for me to have in the future)
    const [products, setProducts] = useState([]);

    useEffect(() => {
        Axios.get('http://localhost:3001/api/get').then((response) => {
            setProducts(response.data);
        });
    }, []);

    const submit = () => {
        Axios.post('http://localhost:3001/api/insert', 
        {Name: Name, Price: Price, Description: Description, Image: Image, Category: Category})

        setProducts([
            ...products,
            {Name: Name, Price: Price},
        ]);
    }

    return(
        <div className="bg-white text-center">
            <h1>Here will do the Mysql stuff</h1>
            {/* <h2>users table</h2>
            <div className="d-flex justify-content-center align-items-center flex-wrap">
                <label>Username<br></br>
                <input type="text" name="Username" /></label>
                <label>password<br></br>
                <input type="text" name="Password" /></label>
                <label>Email<br></br>
                <input type="text" name="Email" /></label>
                <label>Fullname<br></br>
                <input type="text" name="Fullname" /></label>
                <label>Address<br></br>
                <input type="text" name="Address" /></label>
                <label>Phone<br></br>
                <input type="text" name="Phone" /> </label>
            </div>
            <h2>cart table</h2>
            <div>
                <label>CartID<br></br>
                <input type="text" name="CartID" /></label>
                <label>UserID<br></br>
                <input type="text" name="UserID" /></label>
                <label>ProductID<br></br>
                <input type="text" name="ProductID" /></label>
            </div> */}
            <h2>products table</h2>
            <div className="mb-5">
                <label>ProductID(I think it will be increment alone without me typing it)<br></br>
                <input type="text" name="ProductID" onChange={(e) => {setProductID(e.target.value)}} /></label>
                <label>Name<br></br>
                <input type="text" name="Name" onChange={(e) => {setName(e.target.value)}} /></label>
                <label>Price<br></br>
                <input type="text" name="Price" onChange={(e) => {setPrice(e.target.value)}} /></label>
                <label>Description<br></br>
                <input type="text" name="Description" onChange={(e) => {setDescription(e.target.value)}} /></label>
                <label>Image<br></br>
                <input type="text" name="Image" onChange={(e) => {setImage(e.target.value)}} /></label>
                <label>Category<br></br>
                <input type="text" name="Category" onChange={(e) => {setCategory(e.target.value)}} /></label>
            </div>
            <button onClick={submit}>Submit</button>

            {products.map((val) => {
                return <h1>Name: {val.Name} | Price: {val.Price}</h1>
            })}
        </div>
    )
}

export function Home() {
    return (
        <>
        <UpperSide />
        <UpperSideSecond />
        <Slideshow></Slideshow>
        <Main></Main>
        <Footer year={new Date().getFullYear()}></Footer>
        </>
    )
}

export function Whoops404() {
    let location = useLocation();

    return(
        <div>
            <h1>Resource not found at {location.pathname}!</h1>
        </div>
    )
}
