import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
// import App from './App';
import './App.css';

function UpperSide() {
return (
  <div>
  <p>MEDIEVAL PIZZA</p>
  <p>We make food with love, not love with food</p>
  <h2>We are now open for ONLINE ORDERS!</h2>
  </div>);
}

function Button() {
return (
  <button>Meniu & Comanda</button>
);
}


function Slideshow() {
  const pictures = ["https:\/\/medievalpizza.com\/wp-content\/uploads\/2021\/04\/341-1-scaled.jpg", "https:\/\/medievalpizza.com\/wp-content\/uploads\/2021\/04\/226-1-scaled-e1617704481484.jpg", "https:\/\/medievalpizza.com\/wp-content\/uploads\/2021\/04\/12183-scaled-e1617704565414.jpg", "https:\/\/medievalpizza.com\/wp-content\/uploads\/2021\/04\/8117-scaled.jpg"];
  const delay = 2500;
  const [index, setIndex] = React.useState(0);
  const timeoutRef = React.useRef(null);

  function resetTimeout() {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
  }

  React.useEffect(() => {
    resetTimeout();
    timeoutRef.current = setTimeout(
      () =>
        setIndex((prevIndex) =>
          prevIndex === pictures.length - 1 ? 0 : prevIndex + 1
        ),
      delay
    );

    return () => {
      resetTimeout();
    };
  }, [index]);

  return (
    <div className="slideshow">
      <div
        className="slideshowSlider"
        style={{ transform: `translate3d(${-index * 100}%, 0, 0)` }}
      >
        {pictures.map((img, index) => (
          <img
            className="slide"
            key={index}
            src={img}
            alt="new">
            </img>
            // style={{ img }}
        ))}
      </div>

    </div>
  );
}

function App() {
  return (
    <>
    <UpperSide></UpperSide>
    <Button></Button>
    <Slideshow></Slideshow>
    </>
  );
}

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);

export default App;
// If you want to start measuring performance in your app, pass a function
